import React from "react";
import GoogleLogin from "react-google-login";
import { FcGoogle } from "react-icons/fc";
import mievidenciaVideo from "../assets/mievidencia.mp4";
import logo from "../assets/logowhite.png";

import { useEffect } from "react";

import { gapi } from "gapi-script";

export const Login = () => {
  useEffect(() => {
    const start = () => {
      gapi.client.init({
        clientId:
          "763616606255-diebl013r4ab2lf7n8n1dmod84hc67b0.apps.googleusercontent.com",
        scope: "",
      });
    };
    gapi.load("client:auth2", start);
  }, []);

  const responseGoogle = (response) => {
    // console.log(response);
    localStorage.setItem("user", JSON.stringify(response.profileObj));
    const { name, googleId, imageUrl } = response.profileObj;
    const doc = {
      _id: googleId,
      _type: "user",
      userName: name,
      image: imageUrl,
    };
  };

  return (
    <div className="flex justify-start items-center flex-col h-screen">
      <div className="relative w-full h-full">
        <video
          src={mievidenciaVideo}
          type="video/mp4"
          loop
          controls={false}
          muted
          autoPlay
          className="w-full h-full object-cover"
        ></video>
        <div className="absolute flex flex-col justify-center items-center top-0 right-0 left-0 bottom-0 bg-blackOverlay">
          <div className="p-5">
            <img src={logo} width="130px" alt="logo"></img>
          </div>
          <div className="shadow-2xl">
            <GoogleLogin
              clientId="763616606255-diebl013r4ab2lf7n8n1dmod84hc67b0.apps.googleusercontent.com"
              render={(renderProps) => (
                <button
                  type="button"
                  className="bg-mainColor flex justify-center items-center p-3 rounded-lg cursor-pointer outline-none"
                  onClick={renderProps.onClick}
                  disabled={renderProps.disabled}
                >
                  <FcGoogle className="mr-4" /> Sign in with google
                </button>
              )}
              onSuccess={responseGoogle}
              onFailure={responseGoogle}
              cookiePolicy="single_host_origin"
            />
          </div>
        </div>
      </div>
    </div>
  );
};
