import React from "react";
import GoogleLogin from "react-google-login";
// import { useNavigate } from "react-router";
import { FcGoogle } from "react-icons/fc";
import mievidenciaVideo from "../assets/mievidencia.mp4";
import logo from "../assets/logowhite.png";

import { useEffect, useState } from "react";

import { gapi } from "gapi-script";

export const Login = () => {
  const [expanded, setExpanded] = useState(false);
  useEffect(() => {
    const start = () => {
      gapi.client.init({
        clientId:
          "763616606255-diebl013r4ab2lf7n8n1dmod84hc67b0.apps.googleusercontent.com",
        scope: "",
      });
    };
    gapi.load("client:auth2", start);
    const onOutsideClick = (event) => {
      if (navbarRef.current && !navbarRef.current.contains(event.target)) {
        setExpanded(false);
      }
    };

    document.addEventListener("click", onOutsideClick);
  }, []);

  const responseGoogle = (response) => {
    console.log(response);
  };

  return (
    <div className="flex justify-start items-center flex-col h-screen">
      <div className="relative w-full h-full">
        {/* <GoogleLogin
              clientId="763616606255-diebl013r4ab2lf7n8n1dmod84hc67b0.apps.googleusercontent.com"
